import React, {Component} from 'react';
import './SummaryTable.css';

const title_head = ["Date", "Process", "Input Beans", "Input Weight", "Output Beans", "Output Weight"]
const option_title = ["Date", "Process", "Input Weight", "Output Weight"]
function filter_list(process){
  option_title.map((option) => {
  if(option === "Date"){
    return (
      <div className="filter-element">
        <div className="option-subtitle">{option}</div>
        <input type="date"></input>
        <div>to</div>
        <input type="date"></input>
      </div>
    )
  }
  else if(option === "Process"){
    if(process !== undefined){
      return (
        <div className="filter-element">
          <div className="option-subtitle">{option}</div>
          <select>
            <option value={process}>{process}</option>
          </select>
        </div>
      )
    }
    else{
      return (
        <div className="filter-element">
          <div className="option-subtitle">{option}</div>
          <select>
            <option value="buy">Buy</option>
            <option value="roast">Roast</option>
            <option value="winnow">Winnow</option>
            <option value="grind">Grind</option>
            <option value="package">Package</option>
          </select>
        </div>
      )
    }
  }
  else if(option === "Input Weight" || option === "Output Weight"){
    return (
      <div className="filter-element">
        <div className="option-subtitle">{option}</div>
        <input type="text" placeholder="Please enter weight(kg)..."/>
      </div>
    )
  }
})};

class SummaryTable extends Component {
  render() {
    const category = this.props.category;
    return (
      <div className="table-container">
        <div className="page-title">Summary Table</div>
        <div className="filter-container">
          <div className="filter-subtitle">Filter</div>
          {filter_list}
          <div className="enter element">
            <button className="enter_button">Submit</button>
          </div>
        </div>
        <div className="data-container">
          <table className="raw-material-output-table" border="1">
            <tr bgcolor="#945600" style={{color: "white"}}>
              {title_head.map((title_head => 
                <td>{title_head}</td>
              ))}
            </tr>

            <tr bgcolor="#CE8147">
              <td>Total</td>
              <td>0</td>
              <td>0</td>
              <td>0</td>
              <td>0</td>
              <td>0</td>
              <td>0</td>
            </tr>
          </table>
        </div>
      </div>


    )
  };
}

export default SummaryTable;
